import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function Search() {
  const [results, setResults] = useState([]);
  const { params } = useParams();

  useEffect(() => {
    async function fetchData() {
      const res = await fetch(
        `http://localhost:8000/api/imslp?q=${encodeURIComponent(params)}`
      );
      const data = await res.json(); // this gives an object of objects

      data && setResults(data.query.search);
    }
    fetchData();
  }, []);

  console.log(results);

  return (
    <div className="h-fit w-full
    flex flex-col gap-6">
      <p className="text-sm uppercase font-light">
        Search results for "{params}"
      </p>
      <ul className="borger-b-[]">
        {results.map((item) => (
          <li className="py-2" key={item.id}>
            <h1 className="text-xl font-bold text-black/70">{item.title}</h1>
          </li>
        ))}
      </ul>
    </div>
  );
}
