import { FaPlus } from "react-icons/fa6";
import { FaSearch } from "react-icons/fa";
import { MdCancel } from "react-icons/md";
import { useState, useEffect } from "react";
export default function SearchBar({ setIsSearching }) {
  const [searchParams, setSearchParams] = useState("");
  const [result, setResult] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();

    /* try {
      const res = await fetch("/api/imslp");
      if (!res.ok) {
        throw new Error(`server error!`);
      }

      const data = await res.json();
      setResult(data);

    } catch (err) {
      console.error("Fetch failed:", err);
    } */

    const r = await fetch(
      "https://imslp.org/api.php?action=query&list=search&format=json&srsearch=Fur+Elise"
    );
    const data = await r.json();
    console.log(data.query.search);
  }

  // console.log(result);

  return (
    <div className="flex flex-row sm:gap-2">
      <button className="text-black/20" onClick={() => setIsSearching(false)}>
        <MdCancel />
      </button>
      <form
        className="relative flex justify-center items-center"
        onSubmit={handleSubmit}
      >
        <input
          type="text"
          value={searchParams}
          onChange={(e) => setSearchParams(e.target.value)}
          className="home-search-input"
          placeholder="search sheets..."
        />
        <button type="submit" className="absolute right-2 text-black/20">
          <FaSearch />
        </button>
      </form>
    </div>
  );
}
