import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function Search() {
  const [results, setResults] = useState([]);
  const { params } = useParams();



  useEffect(() => {

    async function fetchData() {
      const res = await fetch(
        `http://localhost:8000/api/imslp?q=${encodeURIComponent(params)}`
      );
      const data = await res.json(); 

      data && setResults(data.query.search);
    }
    fetchData();
  }, []);


  return (
    <div
      className="h-fit w-full
    flex flex-col gap-6"
    >
      <p className="text-sm uppercase font-light">
        Search results for "{params}"
      </p>
      <ul className="gap-4">
        {results.map((item) => {

          return (
            <li className="py-2 text-black/70 border-b-[2px] border-black/10" key={item.snippet}>
              <h1 className="text-xl font-bold ">
                {item.title}
              </h1>
             
            </li>
          );
        })}
      </ul>
    </div>
  );
}
