import { FaPlus } from "react-icons/fa6";
import { FaSearch } from "react-icons/fa";
import { MdCancel } from "react-icons/md";
import { NavLink } from "react-router-dom";
import { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
export default function Header() {
  const [isSearching, setIsSearching] = useState(false);
  const [searchParams, setSearchParams] = useState("");
  console.log(searchParams)

  return (
    <header className="w-full max-w-[55rem]">
      <nav
        className="w-full flex flex-row justify-between
      pt-4"
      >
        {/** ---- left nav div ------- */}
        
        <div className={`flex flex-row justify-between gap-2 md:gap-4
         
          `}>
          <NavLink
            to={"/"}
            className={({ isActive }) =>
              isActive ? "btn-primary" : "btn-secondary"
            }
          >
            <button>Home</button>
          </NavLink>
          <NavLink
            to={"community"}
            className={({ isActive }) =>
              isActive ? "btn-primary" : "btn-secondary"
            }
          >
            <button>Community</button>
          </NavLink>
        </div>

        {/** ---- rightnav div ------- */}
        {isSearching ? (
          <div className="flex flex-row sm:gap-2">
            <button className="text-black/20" onClick={()=>setIsSearching(false)}>
              <MdCancel />
            </button>
            <form className="relative flex justify-center items-center">
              <input 
              type="text"
              value={searchParams}
              onChange={(e)=>setSearchParams(e.target.value)}
              className="home-search-input"
              placeholder="search sheets..."
               />
              <button type="submit" className="absolute right-2 text-black/20">
                <FaSearch />
              </button>
            </form>
          </div>
        ) : (
          <div className="flex flex-row gap-4">
            <button className="btn-tertiary" onClick={()=>setIsSearching(true)}>
              <FaSearch />
            </button>
            <button className="btn-tertiary">
              <FaPlus />
            </button>
          </div>
        )}
      </nav>
    </header>
  );
}
