import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

export default function Search() {
  const [results, setResults] = useState([]);
  const { params } = useParams();

  useEffect(() => {
    async function fetchData() {
      const res = await fetch(
        `http://localhost:8000/api/imslp?q=${encodeURIComponent(params)}`
      );
      const data = await res.json(); // this gives an object of objects

      data && setResults(data.query.search);
    }
    fetchData();
  }, []);

  console.log(results);

  return (
    <div
      className="h-fit w-full
    flex flex-col gap-6"
    >
      <p className="text-sm uppercase font-light">
        Search results for "{params}"
      </p>
      <ul className="">
        {results.map((item) => {
            const reg = new RegExp("<span(.*?/)>","gms")
            const trimmedSnippet = item.snippet.replace(reg, "")
            
          return (
            <li className="py-4" key={item.snippet}>
              <h1 className="text-xl font-bold text-black/70 border-b-[2px] border-black/10">
                {item.title}
              </h1>
              <p>{trimmedSnippet}</p>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
