import { FaSearch } from "react-icons/fa";
import { MdCancel } from "react-icons/md";
import { useState, useEffect } from "react";

export default function SearchBar({ setIsSearching }) {
  const [searchParams, setSearchParams] = useState("");
  const [matchingResult, setMatchingResult] = useState(null);
  const [r, setR] = useState("");

  async function fetchData(searchParams) {
    const res = await fetch("http://localhost:8000/api/imslp");
    const data = await res.json(); // this gives an object of objects

    // use Object.entries() to break data into an array of ararys
    // each smaller array is [key, value]
    /* const results = Object.entries(data).filter(([key, value]) =>
      //value.id.toLowerCase().includes(searchParams.toLowerCase())
      
    ); */
    const results = Object.entries(data)
    setR(results);
  }

  useEffect(() => console.log(r), [r]);

  function handleChange(value) {
    setSearchParams(value);
    fetchData(searchParams);
  }

  return (
    <div className=" flex flex-col">
      {/** ------ search bar ------ */}
      <div className="flex flex-row sm:gap-2 h-full">
        <button className="text-black/20" onClick={() => setIsSearching(false)}>
          <MdCancel />
        </button>
        <form className="relative flex justify-center items-center">
          <input
            type="text"
            value={searchParams}
            onChange={(e) => handleChange(e.target.value)}
            className="home-search-input"
            placeholder="search sheets..."
          />
          <button type="submit" className="absolute right-2 text-black/20">
            <FaSearch />
          </button>
        </form>
      </div>
      {/** ------ search result ------ */}
      {matchingResult && (
        <ul>
          {matchingResult.map((r) => (
            <li>{r.id}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
